import React from 'react';
import '../App.css';
// import createCont from '../context/issuesContext';
//import { Link } from "react-router-dom";

function Issues ({postagem}) {
  let title = postagem.title;
  let comments = postagem.comments;
  let state = postagem.state === "open" ? "Aberto" : "Fechado";
  let lab = postagem.labels.map((element) => element.name);
  let created = postagem.created_at;

  const mapLabels = lab.map((element) => element.name)

  if(postagem.index % 2 === 0) {
    return(
      <div className='postsPar'>
      <h1 className='titulo'>{title}</h1>
      <p className='comentarios'> Comentários: {comments}</p>
      <p className='estado'>Estado: {state}</p>
      <p className='label'>Labels: {mapLabels}</p>
      <p className="created">Data de Criação: {created}</p>
      </div>
    )
    } else {
      return(
        <div className='postsImpar'>
        <h1 className='titulo'>{title}</h1>
        <p className='comentarios'> Comentários: {comments}</p>
        <p className='estado'>Estado: {state}</p>
        <p className='label'>Labels: {mapLabels}</p>
        <p className="created">Data de Criação: {created}</p>
        </div>
      )
    }
}

export default Issues;
