import React, { useState, useEffect } from 'react';
import Issues from './Issues';

function Comp(){
  const [data, setData] = useState([]);
  const [selected, setSelected] = useState('');
  const [clicked, setClicked] = useState('');
  const [status, setStatus] = useState('open');
  const [statusClicked, setStatusClicked] = useState('open');
  const [label, setLabel] = useState('');
  const [labelClicked, setLabelClicked] = useState('');
  const [page, setPage] = useState('1');

  function apiFetch() {
    const fetchApi = fetch("https://api.github.com/repos/facebook/react/issues")
      .then((response) => response.json())
        .then((result) => {
            const empty = [];
            result.map((elem) => {
              const elemLimpo = {
                title: elem.title ,
                labels: elem.labels,
                comments: elem.comments,
                state: elem.state,
                created_at: elem.created_at,
                index: elem.index,
              }
              empty.push(elemLimpo);
            });
            return setData(empty)
          });
    return fetchApi;
  }

  function mapComments() {
    data.sort(function(a,b) {
      return b.comments > a.comments ? 1 : a.comments > b.comments ? -1 : 0;
  });
  setData([...data]);
  }

  function mapOlders() {
    data.sort(function(a,b) {
      return a.created_at < b.created_at ? -1 : a.created_at > b.created_at ? 1 : 0;
  });
  setData([...data]);
  }

  function mapNews() {
    data.sort(function(a,b) {
      return b.created_at < a.created_at ? -1 : b.created_at > a.created_at ? 1 : 0;
  });
  setData([...data]);
  }

  function handleChange({ target }){
    const { value } = target;
    setSelected( value );
  }

  function statusChange({ target }){
    const { value } = target;
    setStatus( value );
  }

  function labelChange({ target }){
    const { value } = target;
    setLabel( value );
  }

  function statusClick(){
    setStatusClicked(status);
  }

  function handleClick(){
    setClicked(selected);
  }

  function labelClick(){
    setLabelClicked(label);
  }

  function pagesChange({ target }){
    const { value } = target;
    setPage(value);
    console.log(page);
  }

  useEffect(() => {
    apiFetch();
  },[]);

  useEffect(() => { 
    if (clicked === 'comentados'){
      console.log(data);
      mapComments();
      }
    if (clicked === 'antigos'){
      mapOlders();
    }
    if (clicked === 'recentes'){
      mapNews();
    }
  },[clicked])

  useEffect(() => {
    const map = [];
    data
      .forEach((element) => {
        const filtered = element.labels
          .filter((elem) => elem['name'].includes(label))

        if (filtered.length !== 0) {
          map.push(element);
        }
      });

    setData(map);
  }, [labelClicked])

    if (page === "1") {
      return(
      <div className="divInicial">
        <h5>Selecionar Página</h5>
        <input type="radio" value="1" name="paginas" id="pag1" onChange={pagesChange} />1
        <input type="radio" value="2" name="paginas" id="pag2" onChange={pagesChange} />2
        <input type="radio" value="3" name="paginas" id="pag3"onChange={pagesChange} />3
        <label for="label-input">Labels:
        <input data-testid="label-input"
            id="label-input"
            value={ label }
            onChange={ labelChange } />
        </label>
        <button onClick = {labelClick}>Pesquisar Labels</button>
        <label for="status-input">Status:
        <select name="status"
            data-testid="status-input"
            id="status-input"
            value={ status }
            onChange={ statusChange }>
              <option value="open">Abertos</option>
              <option value="closed">Fechados</option>
            </select>
        </label>
        <button onClick = {statusClick}>Filtrar</button>
        <label for="selected-input">Ordenar por:
      <select name="selected"
            data-testid="selected-input"
            id="selected-input"
            value={ selected }
            onChange={ handleChange }>
        <option value="comentados">Mais comentados</option>
        <option value="recentes">Mais Recentes</option>
        <option value="antigos">Mais Antigos</option>
      </select>
      </label>
      <button onClick= {handleClick}>Ordenar</button>
      {
        data.slice(0,10).filter(
          (element) => element.state === statusClicked)
            .map((post, index) => (
            <Issues key={index} postagem={post} />
          ))
      }
  </div>
    )
    } else if (page === '2') {
      return(
        <div className="divInicial">
          <h5>Selecionar Página</h5>
          <input type="radio" value="1" name="paginas" id="pag1" onChange={pagesChange} />1
          <input type="radio" value="2" name="paginas" id="pag2" onChange={pagesChange} />2
          <input type="radio" value="3" name="paginas" id="pag3"onChange={pagesChange} />3
          <label for="label-input">Labels:
          <input data-testid="label-input"
              id="label-input"
              value={ label }
              onChange={ labelChange } />
          </label>
          <button onClick = {labelClick}>Pesquisar Labels</button>
          <label for="status-input">Status:
          <select name="status"
              data-testid="status-input"
              id="status-input"
